import {
    ADD_CONTACT,
    GET_CONTACTS,
    CLEAR_CONTACTS,
    DELETE_CONTACT,
    SET_CURRENT,
    CLEAR_CURRENT,
    UPDATE_CONTACT,
    FILTER_CONTACTS,
    CLEAR_FILTER,
    CONTACT_ERROR
} from '../types'

const contactReducer = (state, action) => {
    switch(action.type) {
        case GET_CONTACTS:
            return {
                ...state,
                contacts: action.payload,
                loading:false
            }
        case ADD_CONTACT:
            return {
                ...state,
                contacts: [...state.contacts, action.payload]
            }
        case CONTACT_ERROR:
            return {
                ...state,
                error: action.payload
            }    
        case UPDATE_CONTACT:
            return {
                ...state,
                contacts: state.contacts.map(contact => contact._id === action.payload._id ? action.payload: contact) 
            }    
        case DELETE_CONTACT:
            return{
                ...state,
                contacts: state.contacts.filter(contact => contact._id !== action.payload)
            }
        case SET_CURRENT:
            return{
                ...state,
                current: action.payload
            }
        case CLEAR_CURRENT:
            return{
                ...state,
                current:null,
                contacts:null

            }        
        case FILTER_CONTACTS:
            return {
                ...state,
                filtered: state.contacts.filter(contact => {
                const regex = new RegExp(`${action.payload}`, 'gi');
                return contact.name.match(regex) || contact.email.match(regex);
                })
            }
        case CLEAR_CONTACTS:
            return {
                ...state,
                contacts:null,
                filtered: null,
                error:null,
                current:null
            }    
        case CLEAR_FILTER:
            return {
                ...state,
                filtered: null
            }            
        default:
            return state;
    }
}

export default contactReducer;
